O = thisLayer.toWorld(thisLayer.anchorPoint);
W = div(controller.effect('IK leg r')(1), 100);
FK = controller.effect('IK leg r')(3);
R = controller.effect('IK leg r')(2);
var vec = R == 1 ? sub(O, C) : sub(C, O);
var result = sum(value, FK);
var layer = thisLayer;
while (layer.hasParent) {
    layer = layer.parent;
    result = sub(result, layer.rotation);
}
var angle = Math.atan2(vec[1], vec[0]);
angle = radiansToDegrees(angle);
angle = mul(angle, W);
$bm_rt = result += angle;

